INSERT INTO `tbl_provinces` (`id_province`, `name`, `description`) VALUES
(2, 'An Giang', '<p><strong>33 món đặc sản An Giang</strong><strong>&nbsp;được tổng hợp từ tất cả các đặc sản nổi tiếng nhất của An Giang như đặc sản khô An Giang, trái cây, bánh kẹo… Đến từ Châu Đốc, Tân Châu, An Phú, Tri Tôn, Chợ Mới…</strong></p>\r\n<p>Có thể nói An Giang là mảnh đất đặt biệt nhất ở vùng đồng bằng sông Cửu Long. Vừa mang sự đặc trưng của vùng sông nước miền Tây cho nhiều tôm cá, cây trái xum xuê. Nhưng cũng là nơi có linh khí trời đất với núi cao rừng thẳm cho nhiều sản vật vùng cao.</p>\r\n<p>Cùng với đó là sự giao thoa của nhiều cộng đồng người dân khác nhau. Mà tiêu biểu là người Chăm với văn hóa</a> Óc Eo hàng ngàn năm. Cùng người Hoa, người Khmer, người Việt,… tạo nên một cộng đồng dân cư đa dạng.</p>\r\n<p>Bởi thế mà một khi hỏi&nbsp;<strong>đặc sản An Giang là gì</strong>? Thì khó mà liệt kê cho hết được. Nào là&nbsp;<em>đặc sản khô An Giang</em>,&nbsp;<em>đặc sản dân tộc Chăm ở An Giang</em>, trái cây, thốt nốt… Đến các món ăn đặc sản của An Giang gắn liền với các địa phương như: Đ<em>ặc sản Tân Châu</em>,&nbsp;<em>đặc sản An Phú</em>,&nbsp;<em>đặc sản chợ Mới</em>,&nbsp;<em>đặc sản Phú Tân</em>,&nbsp;<em>đặc sản Tri Tôn</em>,…</p>'),
(3, 'Bà Rịa – VT', 'Tỉnh Bà Rịa – Vũng Tàu không chỉ lôi cuốn du khách gần xa bởi cảnh quan tươi đẹp mà còn bởi có nhiều món ăn hấp dẫn, hiếm nơi nào có được. Mỗi món ăn ở đây đều gắn liền với từng địa danh, xuất xứ và mang những hương vị đặc trưng riêng. Một số đặc sản như: Bánh hỏi An Nhất, Mứt hạt bàng Côn Đảo, Gỏi cá mai, Bánh xèo biển Long Hải, Lẩu súng Phước Hải, Tiết canh tôm hùm Long Hải, Bánh khọt Vũng Tàu, Cháo Hàu Long Sơn, Bánh bông lan trứng muối.'),
(4, 'Bạc Liêu', '<p>TỔNG HỢP ĐẶC SẢN VĨNH LONG: TẤT CẢ 20 ĐẶC SẢN NỔI TIẾNG CỦA VĨNH LONG</p>\r\n<p>Tất cả 20 đặc sản Vĩnh Long đều được tổng hợp hết trong bài viết này. Với đủ hết các đặc sản của Vĩnh Long nổi tiếng và gây thương nhớ nhất.</p>\r\n<p>Vùng đất trù phú Vĩnh Long được bồi đắp bởi hai con sông lớn nhất đồng bằng sông Cửu Long: sông Tiền và sông Hậu. Nguồn nước dồi dào ngày ngày nuôi dưỡng nên biết bao thức ngon quả ngọt cho miệt thứ này.</p>\r\n<p>Ghé thăm Vĩnh Long người ta không chỉ được chiêm ngưỡng cảnh đẹp đậm chất miền Tây dân dã bao mà còn được ăn món ngon không phải xứ nào cũng có. Còn bạn, bạn đã sẵn sàng cùng VietFlavour khám phá xem những món đặc sản ở Vĩnh Long ngon tới mức nào chưa?</p>'),
(5, 'Bắc Kạn', 'Ngoài cảnh đẹp non nước hữu tình của hồ Ba Bể, Bắc Kạn còn có những đặc sản dân tộc đặc trưng khiến bước chân lữ khách du lịch nơi đây phải lưu luyến. Đến với Bắc Kạn, các bạn không những có cơ hội tham quan khung cảnh thiên nhiên tuyệt đẹp mà còn được thưởng thức các loại đặc sản mang đậm bản sắc của đồng bào dân tộc nơi đây.'),
(6, 'Bắc Giang', '<p>TỔNG HỢP ĐẶC SẢN BẮC GIANG: TẤT CẢ 27 ĐẶC SẢN NỔI TIẾNG CỦA BẮC GIANG</p>\r\n<p>Tất cả 27 đặc sản Bắc Giang được tổng hợp đều là những món ngon vật lạ, đặc sản nổi tiếng ở Bắc Giang sẽ khiến cho du khách lưu luyến mãi đất Kinh Bắc.</p>'),
(7, 'Bắc Ninh', '<p>Bắc Ninh là tỉnh có diện tích nhỏ nhất Việt Nam song lại có một nền văn hóa đặc sắc. Nơi đây hằng năm có trên 300 lễ hội dân gian, nhiều di tích lịch sử và những làng nghề truyền thống. Trong số rất nhiều những làng nghề truyền thống đó, khiến du khách đã thưởng thức rồi chẳng muốn về là quê hương của những làn điệu dân ca quan họ ngọt ngào, có những nơi đã sản sinh ra loại đặc sản thơm ngon độc nhất, níu chân du khách bởi những đặc sản ngon nức tiếng. Bắc Ninh có rất nhiều món ngon như: thịt chuột Đình Bảng, nem làng Bùi, gà Hồ, bánh phu thê…khiến bạn không thể cưỡng nổi khi tới nơi đây.</p>'),
(8, 'Bến Tre', '<p>TỔNG HỢP ĐẶC SẢN BẾN TRE: TẤT CẢ 25 ĐẶC SẢN XỨ DỪA BẾN TRE</p>\r\n<p>Đặc sản Bến Tre có gì ngoài DỪA? Tất cả 25 đặc sản Bến Tre đều được tổng hợp trong bài viết này sẽ giúp bạn khám phá hết văn hóa ẩm thực xứ dừa Bến Tre.</p>\r\n<p>Bến Tre nơi được mệnh danh là xứ sở của cây dừa. Với những hàng dừa trải dài thẳng tắp, những con người thân thiện, hiếu khách đã có nhiều năm chung sống và gắn bó với cây dừa nên ẩm thực nơi đây phần lớn đều được chế biến từ dừa như kẹo dừa, nước dừa, cơm dừa, củ hũ dừa, nước cốt dừa cho đến vị béo ngậy của đuông dừa và còn rất nhiều các món ăn đặc sản khác từ dừa.</p>'),
(9, 'Bình Dương', 'Bình Dương luôn thu hút khách du lịch bởi những vườn trái cây trĩu quả và các món ăn ngon đậm đà bản sắc Nam Bộ. Những quán ăn ngon nức tiếng ở Bình Dương luôn là địa chỉ thu hút đông đảo thực khách đến đây thưởng thức các món hấp dẫn, độc đáo được chế biến theo những công thức đặc biệt. Bên cạnh những đặc sản thơm ngon nức tiếng, ở Bình Dương còn có vô vàn món ngon, \'ăn là ghiền\' khiến nhiều bạn trẻ \'xiêu lòng\'.'),
(10, 'Bình Định', 'Bình Định là một địa danh nổi tiếng với khách du lịch Việt Nam và quốc tế. Không chỉ gây ấn tượng bởi những danh lam thắng cảnh, di tích văn hóa đặc sắc, nơi đây còn \"quyến rũ\" bởi nền ẩm thực phong phú. Đến với miền đất võ, bạn sẽ có cơ hội được thưởng thức những món đặc sản vô cùng thơm ngon và bổ dưỡng. Là một trong những tỉnh thuộc dãy đất miền Trung, nền ẩm thực Bình Định được xem là một trong những nét tiêu biểu nhất của văn hóa ẩm thực Trung Bộ nói chung. Vậy điểm hấp dẫn trong ẩm thực Bình Định là gì? Cùng tìm hiểu trong bài viết sau đây nhé!'),
(11, 'Bình Phước', 'Đến Bình Phước, người ta không chỉ được thăm thú những cảnh đẹp, tìm hiểu văn hóa người dân nơi đây mà còn bị say mê bởi những món đặc sản ngon không thể chối từ. Bình Phước nổi tiếng với những món ngon không trộn lẫn với những vùng quê khác như: gỏi trái điều, bánh hạt điều, hạt điều rang muối, ve sầu chiên giòn...và nhiều món ăn độc đáo khác.'),
(12, 'Bình Thuận', 'Mảnh đất Bình Thuận có nhiều món ăn đặc sản cực ngon mà bất cứ du khách nào đã từng tới du lịch đều vương vấn. Du lịch Bình Thuận bạn sẽ được thưởng thức rất nhiều món đặc sản ngon ở vùng đất nắng và gió này. Những nguyên liệu quen thuộc như hải sản, bánh tráng, lòng heo... được người dân Bình Thuận chế biến ra những món ăn ngon trở thành đặc sản của riêng vùng đất này. Sau đây là những đặc sản Bình Thuận ngon nức tiếng mà bạn nên một lần thưởng thức nếu có dịp đặt chân tới đây nhé!'),
(13, 'Cà Mau', '<p>TỔNG HỢP ĐẶC SẢN CÀ MAU: TẤT CẢ 20 ĐẶC SẢN NỔI TIẾNG CỦA MIỀN ĐẤT MŨI</p>\r\n<p>Cà Mau có đặc sản gì? Vietflavour đã tổng hợp tất cả các đặc sản Cà Mau và xin giới thiệu tới bạn đọc 20 đặc sản không chỉ nổi tiếng mà còn gây thương nhớ nhất của nơi “cùng trời cuối đất”.</p>'),
(14, 'Cao Bằng', 'Cao Bằng gạo trắng nước trong! Đến Cao Bằng, được thưởng thức những đặc sản do người dân vùng núi rừng Đông Bắc thân thiện và vô cùng mến khách chế biến sẽ khiến ai đã một lần là nhớ mãi...Còn gì tuyệt vời hơn khi du lịch đến chốn núi rừng Tây Bắc – Cao Bằng gạo trắng nước trong để thưởng thức ngay những đặc sản của người dân nơi đây. Vùng đất Cao Bằng có quá nhiều những món ngon đặc sản khiến bạn khó có thể quên. Có những món bạn nên thưởng thức ngay trong chuyến đi nhưng có những món bạn có thể đem về làm quà cho bạn bè, người thân.'),
(15, 'Cần Thơ', 'Đi Cần Thơ nếm tròn vị các món bánh cống, cá lóc nướng trui, bún mắm,… có một cái gì đó làm người ta luôn cảm thấy quyến luyến. Du lịch Cần Thơ gạo trắng nước trong cùng với văn hóa ẩm thực phong phú đa dạng luôn biết cách khiến du khách phải nao lòng. Thứ nhất, dù đi một mình, du khách đi Cần Thơ cũng không bao giờ cảm thấy lạc lõng vì chỉ cần cởi mở một chút, lân la hỏi chuyện người bản xứ là chắc chắn nhận về cả một kho những câu chuyện thú vị không để đâu cho hết. Thứ hai, đi thuyền trên sông nước mênh mông khi đi Cần Thơ chưa bao giờ nhàm chán, ngay cả người ở những nước tận đẩu tận đâu cũng tìm đến miền Tây để chèo thuyền cơ mà. Và thứ ba, quan trọng nhất, đồ ăn cực rẻ và cực ngon.'),
(16, 'Đà Nẵng', 'Không phải ngẫu nhiên mà Đà Nẵng được mệnh danh là thành phố đáng sống nhất Việt Nam. Vùng đất này chinh phục trái tim của biết bao du khách đến đây nhờ phong cảnh tuyệt đẹp, khí hậu trong lành, nhiều địa điểm vui chơi thú vị mà còn có đến hàng trăm món ăn ngon người dân thân thiện và tất nhiên là không thể thiếu những món ăn đặc sản chỉ Đà Nẵng mới có. Quán ăn ngon níu chân du khách, nhất là các bạn trẻ yêu thích khám phá và thưởng thức những món ăn ngon.'),
(17, 'Đắk Lắk', 'Khi nhắc tới Đắk Lắk nhiều người chỉ nhớ tới núi rừng Tây Nguyên hùng vĩ, đất đỏ bazan màu mỡ, lễ hội đua voi tưng bừng… mà bỏ quên một nét văn hóa cũng đặc sắc không kém. Đắk Lắk nói riêng và vùng Tây Nguyên nói chung không chỉ hấp dẫn du khách bởi cảnh quan thiên nhiên hùng vĩ mà còn bởi những món đặc sản dân tộc độc đáo làm say lòng du khách. Đắk Lắk giữ trong mình một nền văn hóa ẩm thực phong phú, là kết tinh của các dân tộc. Về đây, du khách sẽ được thưởng thức đủ món ăn ngon như: gà sa lửa, cơm lam, canh chua nấu cá lăng, gỏi rau rừng, gỏi cà đắng...'),
(18, 'Đắk Nông', 'Đến Đắk Nông, không chỉ ngắm những ngọn thác tuyệt đẹp hùng vĩ hay khám phá hệ sinh thái độc đáo của núi rừng. Bạn cũng sẽ được thưởng thức những món ăn đậm chất Tây Nguyên. Mỗi khi nhắc đến Đắk Nông chắc hẳn du khách sẽ nghĩ ngay đến cảnh hoang sơ, hùng vĩ của những thác nước đang ngày đêm ầm ầm tuôn chảy xen lẫn trong những cánh rừng xanh bạc ngàn cùng với nền văn hoá lâu đời của người M’nông. Trong đời sống sinh hoạt của đồng bào M’nông ẩm thực lại ẩn chứa sự độc đáo, mới lạ mang đến cho du khách những dấu ấn không thể nào quên khi ghé thăm vùng đất này, những món ăn phải kể đến là: Rượu cần, cơm lam, canh thụt. Những đặc sản nổi tiếng như cà phê Đức Lập, khoai lang Tuy Đức, trái cây Đắk Glong, cá lăng sông Sêrêpốk, canh thụt đọt mây… mà du khách không nên bỏ lỡ khi có dịp đặt chân đến Đắc Nông.'),
(19, 'Điện Biên', 'Không quá phong phú trong chủng loại nhưng các món ăn ở Điện Biên cực kì độc đáo, có một không hai khiến du khách đến từ đâu cũng sẽ hài lòng. Các món ăn của người dân tộc ở Điện Biên được chế biến rất cầu kỳ, nhất là nghệ thuật sử dụng các loại gia vị độc đáo của núi rừng Tây Bắc. Xôi nếp nương, thịt trâu hun khói, chẳm chéo, bắp cải cuốn nhót xanh, sâu chít, pa pỉnh, măng đắng, rau hoa ban, gạo tám… là những món ngon Điện Biên không lẫn vào đâu được. hay hoa ban là những món ăn đặc sắc khi đặt chân đến vùng đất Điện Biên Phủ lịch sử. Đừng mải vui chơi với những di tích lịch sử, danh lam thắng cảnh ở Điện Biên mà bỏ quên đi rất nhiều món ăn ngon ở Điện Biên các bạn nhé.'),
(20, 'Đồng Nai', 'Những món đặc sản Đồng Nai luôn có sức hút lạ kì đối với du khách. Hơn nữa do ở ngay gần Sài Gòn nên đây cũng là địa điểm được nhiều người lựa chọn cho những chuyến du lịch cuối tuần. Đồng Nai có những món ăn nghe khá dân dã nhưng độ ngon thì khỏi phải bàn, rất phong phú như bưởi Tân Triều, mít Tố Nữ, nấm mối, cơm gà cá mặn… làm mê mẩn du khách gần xa khi có dịp đặt chân đến nơi đây. Một khi bạn đã đến Đồng Nai, bạn chẳng thể bỏ qua được những món ngon dân dã này. Và khi đã rời khỏi vùng đất này, bạn cũng không thể không đem theo những thứ đặc sản ấy về làm quà.'),
(21, 'Đồng Tháp', 'Đồng tháp là một tỉnh thuộc vùng đồng bằng sông cửu long, nổi tiếng với sản vật miền sông nước, vì thế những món ngon nhất của Đồng Tháp gắn liền với mùa nước nổi ở vùng này. Nhắc đến Đồng Tháp hẳn ai cũng nghĩ đến vựa lúa lớn của cả nước, là “vùng đất sen hồng” với những cánh đồng sen bạt ngàn mà ít ai biết rằng Đồng Tháp còn là “không gian ẩm thực miền Tây” với những món ăn dân dã vô cùng độc đáo và hấp dẫn. Đến thăm Đồng Tháp Mười bạn không chỉ được tận hưởng vẻ đẹp của thiên nhiên với những cánh rừng tràm bạt ngàn, những hồ sen bạt ngàn, những vườn cò sân chim hoang sơ của miệt vườn đặc sắc, mà còn có cơ hội được thử những món ngon đặc sắc đầy hương vị đồng quê như cá lóc nướng lá sen, ốc treo giàn bếp, chuột đồng quay lu, nem Lai Vung, hủ tiếu Sa Đéc ….Sau đây là những món ngon mùa nước nổi bạn không nên bỏ lỡ khi đến với Đồng Tháp'),
(22, 'Gia Lai', 'Những đặc sản Gia Lai nổi tiếng làm nên nét văn hóa ẩm thực đặc sắc gây thương nhớ cho khách du lịch. Ở nơi đây bạn không chỉ được ngắm những khu rừng nguyên sinh rộng mênh mông xanh bạt ngàn mà du khách còn được thưởng thức rât nhiều món ăn ngon. Gia Lai được thiên nhiên ưu ái ban tặng những cánh rừng nguyên sinh tuyệt đẹp, nơi đây không chỉ đẹp mà còn mang trong mình những loại đặc sản “ngon tuyệt cú mèo”, làm nên vẻ đẹp ẩm thực Gia Lai như: măng rừng, rau rừng, gà rừng, mật ong rừng… Ngoài ra Gia Lai còn có một số những món đặc sản nổi tiếng khác như: Phở khô Gia Lai, bún mắn cua, muối kiến vàng, cà phê Pleiku, bò một nắng…'),
(23, 'Hà Giang', 'Hà Giang được biết đến là một tỉnh miền núi với nhiều phong cảnh hùng vĩ, phong tục tập quán, lễ hội phong phú. Đến Hà Giang, người ta không chỉ truyền tai nhau về cao nguyên đá, về những cánh đồng tam giác mạch hay những cung đường phượt mà còn về những món ăn khó quên. Ẩm thực Hà Giang rất phong phú và độc đáo với những món ăn lạ, mang hương vị núi rừng luôn để lại ấn tượng khó quên trong lòng du khách như: Thắng cố, bánh tam giác mạch, bánh cuốn hay mật ong bạc hà là những món ăn khiến người ta mê đắm ở vùng đất Hà Giang.'),
(24, 'Hà Nam', 'Hà Nam là một tỉnh nằm ở Tây Nam châu thổ sông Hồng trong vùng kinh tế trọng điểm bắc bộ và là cửa ngõ phía Nam của thủ đô Hà Nội. Tỉnh Hà Nam có vùng đồng bằng với diện tích đất đai màu mỡ, bãi bồi ven sông Hồng, sông Châu, là tiền đề để phát triển sản xuất nông nghiệp hàng hoá, công nghiệp chế biến nông sản thực phẩm và du lịch sinh thái. Với địa hình và đa dạng về các loại cây trồng cũng như hệ sinh thái người dân Hà Nam đã tận dụng những gì tạo hóa ban tặng để chế biến ra các món ăn ngon nổi tiếng trên khắp cả nước và trở thành Những Đặc sản không thể bỏ qua khi ghé thăm Hà Nam.'),
(25, 'Hà Nội', 'Muốn thưởng thức những quán ăn ngon của Hà Nội, đôi khi bạn phải chui vào từng ngóc ngách, từng con phố nhỏ của Hà Nội. Dưới đây là 23 địa chỉ những quán ngon nức tiếng nhất định phải thử ở Hà Nội một lần trong đời: Mỳ vằn thắn Bình Tây, Bánh mỳ sốt vang Trâm, Bánh tráng trộn, Miến trộn mực, Bánh đa trộn Dốc Thọ Lão, Phở chiên giòn, Miến lươn Đông Thịnh, Miến xào cua, Bún thang bún bung, Bún ốc cô Huệ, Bún măng ngan, Bún mọc dọc mùng, Cháo sườn Hàng Bồ, Xôi rán Hàng Điếu, Bánh đúc nóng,...'),
(26, 'Hà Tĩnh', 'Hà Tĩnh là mảnh đất nổi tiếng với nhiều điểm du lịch hấp dẫn. Không những thế, bất cứ du khách nào khi đến đây sẽ đều bị thu hút bởi những món ăn ngon. Mực \'nhảy\' Vũng Áng, thịt dê núi Hương Sơn, bưởi Phúc Trạch, bánh đa vừng... là những đặc sản trứ danh Hà Tĩnh mà bạn không nên bỏ qua nếu có dịp ghé đến mảnh đất Hồng Lam thân thiện và hiếu khách.'),
(27, 'Hải Dương', 'Hải Dương là vùng đất bình dị, gần gũi và các món ngon nơi đây cũng thấm đượm tinh thần quê hương. Những món đặc sản tuy giản dị nhưng mang một hương vị ẩm thực riêng mà không nơi nào có được khiến cho thực khách nào đã từng thử qua đều phải thấy nao lòng. Nhắc đến Hải Dương, người ta không thể không nhắc đến những món ngon của vùng đất này như vải thiều Thanh Hà, bánh đậu xanh, gà Mạnh Hoạch hay bánh đa gấc…'),
(28, 'Hải Phòng', 'Thành Phố Hải Phòng với nhiều món ăn ngon đặc sản khiến du khách nhớ thương chắc chắn sẽ là một điểm đến lý tưởng dành cho những con người có tâm hồn ăn uống phong phú, thích khám phá món ăn mới lạ. Vậy sau đây là một vài món ăn cho bạn có thể thưởng thức khi đến Hải Phòng: Nem Rán Trung Kiên, Chè Thái & Bánh Mì Cay – Đinh Tiên Hoàng, Bánh Đúc Tàu – Cát Dài, Ốc Thủy Dương, Bánh đa cua đồng 48 Lạch Tray, Lẩu cua đồng đường Văn Cao, Bún Tôm Hợi Hợi...'),
(29, 'Hòa Bình', 'Không chỉ có đập thủy điện, Hòa Bình còn nổi tiếng với phong cảnh núi rừng hùng vĩ, vừa hoang sơ kì vĩ, vừa nên thơ hữu tình đặc biệt hơn là các món ăn mang đậm hơi thở của núi rừng Tây Bắc như: Cơm đồ, nhà gác, nước vác, lợn thui,... Những du khách đã từng một lần thăm thú Hòa Bình đều không khỏi trầm trồ ngỡ ngàng và ấn tượng sâu sắc bởi hương vị đậm đà quyến rũ của những món ăn mang đậm nét đặc trưng vùng Tây Bắc nơi đây.'),
(30, 'Hậu Giang', 'Tỉnh Hậu Giang nổi tiếng không chỉ với vùng đất phì nhiêu rất có tiềm năng về du lịch sinh thái mà cũng từ nhiều món đặc sản ngon đặc trưng vùng sông nước.\r\n Nếu có dịp đặt chân đến Hậu Giang, du khách hãy thử qua những đặc sản sau đây như: cháo lòng Cái Tắc, chả cá thác lác Hậu Giang, bưởi năm roi Phú Hữu, bún gỏi già… Các bạn sẽ rất ngạc nhiên về hương vị thơm ngon của những món ăn dân dã này đấy.'),
(31, 'Hưng Yên', 'Thứ nhất kinh kì, thứ nhì Phố Hiến\' - chẳng phải ngẫu nhiên mà người ta phong cho Hưng Yên cái tên như vậy. Nơi đây không chỉ nổi tiếng với những di tích lịch sử văn hóa lâu đời mà còn nổi tiếng bởi những món đặc sản từ dân dã đến công phu \'tiến vua\'. Hưng Yên là một vùng đất nổi tiếng với nhãn lồng, không những thế nơi đây còn nhiều đặc sản khác cũng hấp dẫn không kém như chả gà Tiểu Quan, ếch om Phượng Tường, bên cạnh đó còn có Gà Đông Tảo tiến vua...'),
(32, 'Khánh Hòa', 'Là một tỉnh ven biển có nhiều làng chài nên phong cách ẩm thực ở Khánh Hòa chịu ảnh hưởng sâu sắc từ biển. Khánh Hòa nổi tiếng với những món đặc sản sang chảnh như yến sào hòn Nội, tôm hùm Bình Ba, nai khô Diên Khánh hay sò huyết Thủy Triều…nhưng cũng có rất nhiều món ngon dân dã như bánh tráng xoài, nem nướng Ninh Hòa, bánh căn, bánh ướt…vô cùng hấp dẫn.'),
(33, 'Kiên Giang', 'Kiên Giang là vùng đất xinh đẹp luôn hấp dẫn mọi du khách ghé qua không chỉ bởi những hòn đảo thơ mộng, những phong cảnh hữu tình mà còn bởi những món ăn ngon nổi tiếng. Kiên Giang có nhiều món ăn ngon lạ, dân dã, mang hương vị riêng biệt không lẫn với bất kì nơi nào, đủ sức làm say lòng du khách thập phương. Kiên Giang có một nền ẩm thực vô cùng phong phú với rất nhiều món ăn ngon. Khi đến du lịch Kiên Giang, bạn hãy cố gắng thử hết các món ăn này nhé!'),
(34, 'Kon Tum', 'Kon Tum, ngoài khám phá cảnh đẹp thiên nhiên, bạn cũng nên thưởng thức các món ăn ngon tại vùng đất này. Cũng giống như các thành phố khác, ẩm thực Kon Tum rất đa dạng và phong phú, tại đây có rất nhiều món ăn ngon, hấp dẫn cho bạn tha hồ thưởng thức. Tuy nhiên, để giúp bạn có thể thưởng thức các món ăn ngon, chuẩn vị nhất thì sẽ gợi ý cho bạn một số món ăn ngon, đặc sản nổi tiếng Kon Tum và địa chỉ cụ thể bạn nên tham khảo.'),
(35, 'Lai Châu', 'Khi đến với Lai Châu bên cạnh những danh lam thắng cảnh nổi tiếng thì bạn còn được thưởng thức những món ăn thơm ngon hấp dẫn đậm chất núi rừng và hòa quyện cùng với hương vị rất riêng biệt sẽ đem lại cho bạn những trải nghiệm thú vị. Vùng núi Lai Châu hùng vĩ có rất nhiều món đặc sản ngon mà nghe có vẻ kì lạ nhưng rất tốt cho sức khỏe như lợn cắp nách, thịt khô gác bếp, rêu nướng, nộm rau dớn,… đã làm mê lòng bao thực khách gần xa du lịch miền đất tuyệt đẹp này.'),
(36, 'Lào Cai', 'Lào Cai là một khu du lịch nổi tiếng của miền Bắc, nơi có khung cảnh hoang sơ, trữ tình của rừng núi, với những thắng cảnh đẹp như trong mơ, khí hậu mát mẻ đó là những điều tuyệt vời khiến vùng đất này trở nên quyến rũ lạ thường. Không chỉ đến đây để khám phá, vãn cảnh du khách còn tìm đến những quán ăn ngon để thưởng thức hương vị ẩm thực nơi đây. Nếu đây là chuyến du lịch lần đầu của bạn, chắc chắn sẽ còn nhiều bỡ ngỡ hãy “note” danh sách địa điểm ăn uống dưới đây để khỏi “đi lạc” nhé.'),
(37, 'Lạng Sơn', 'Lạng Sơn có nhiều đặc sản hấp dẫn, từ món mặn đến món ngọt, món chính đến món ăn vặt mà du khách đến đây nhất định nên dành thời gian thưởng thức.Nếu có dịp đến với Lạng Sơn, một tỉnh miền núi với những ngọn núi hùng vĩ, cảnh đẹp nên thơ du khách cũng đừng quên thưởng thức những món ăn đặc sản hay những đồ ăn vặt tiêu biểu cho nền ẩm thực phong phú nơi đây.'),
(38, 'Lâm Đồng', 'Đến với Lâm Đồng, du khách không những sẽ được khám phá những danh thắng đẹp miền cao nguyên trong tiết trời se lạnh mà còn bị mê hoặc bởi rất nhiều món ngon đặc sản. Lâm Đồng không chỉ có Đà Lạt, thác Dambri, những nông trường trà xanh bát ngát mà còn có nhiều món ăn ngon tại Lâm Đồng thơm ngon, hấp dẫn, làm say đắm lòng người. Đến đây, dù có đi đâu, làm gì thì đừng nên bỏ lỡ việc thưởng thức những món ăn dưới đây nhé!'),
(39, 'Long An', '<p>TỔNG HỢP ĐẶC SẢN LONG AN: TẤT CẢ 12 ĐẶC SẢN CỦA LONG AN</p>\r\n<p>Tổng hợp tất cả 12 đặc sản Long An nổi tiếng và gây thương nhớ nhất được VietFlavour sưu tập sẽ giúp bạn nếu là khách phương xa được trải nghiệm hết các đặc sản của Long An. Còn nếu là người con Long An xa quê sẽ được trở về và tự hào với mảnh đất quê hương Long an.</p>'),
(40, 'Nam Định', 'Nam Định là vùng đất nằm ở phía Đông Nam vùng đồng bằng châu thổ sông Hồng, Nam Định đang bảo tồn và lưu giữ nhiều giá trị văn hóa truyền thống đặc sắc, trong đó có các phong tục, tín ngưỡng, lễ hội, làng nghề, di tích lịch sử văn hóa… Ngoài các giá trị di sản văn hóa ấy Nam Định còn có điều kiện phát triển du lịch sinh thái, đặc biệt là du lịch biển, bên cạnh đó nơi đây nổi tiếng với những món ăn phong phú. Vùng đất này luôn mời gọi, chào đón du khách gần xa về khám phá và trải nghiệm.'),
(41, 'Nghệ An', 'Nghệ An là mảnh đất non nước hữu tình đẹp như bức tranh thủy mặc. Vẻ đẹp kỳ thú pha lẫn chút nguyên sơ luôn làm say đắm bất cứ ai đã từng đi qua và dừng chân ghé lại nơi này. Không chỉ có thế, trên mảnh đất hồn hậu, địa linh nhân kiệt đã sản sinh ra nhiều đặc sản mà ai “lỡ” nếm thử một lần cũng vương vấn mãi không thôi. Các món ăn ngon ở Nghệ An mang chất riêng đặc biệt đến nỗi bất cứ ai có cơ hội được thưởng thức đều nhớ mãi không quên.'),
(42, 'Ninh Bình', 'Ninh Bình nổi tiếng với nhiều địa danh du lịch nổi tiếng như khu du lịch Tràng An, Bái Đính, Tam Cốc- Bích Động, nhà thờ đá Phát Diệm… bên cạnh đó không thể không nhắc tới những món ăn ngon, những món ăn đặc sản nổi tiếng của Ninh Bình khi du lịch Ninh Bình được. Và bạn cần biết danh sách quán ăn ngon giá rẻ ở Ninh Bình- Ẩm thực Ninh Bình, và những địa chỉ ăn uống giá rẻ ở Ninh Bình cho bạn tha hồ chọn lựa, bởi vì không phải quán ăn nào cũng ngon và giá cả vừa túi tiền.'),
(43, 'Ninh Thuận', 'Nắng gió đã ngấm vào từng thớ đất của Ninh Thuận để tạo nên những loại đặc sản bình dị nhưng cũng rất hấp dẫn của vùng đất này. Đến với Ninh Thuận bạn sẽ không chỉ được ngắm những bãi biển xinh đẹp mà bạn còn được thưởng thức những món ăn đặc sản, món ăn ngon ở Ninh Thuận không thể bỏ lỡ khi du lịch. Chính vẻ đẹp và sự phong phú của ẩm thực nơi đây đang dần làm nên thương hiệu cho du lịch Ninh Thuận. Những ai đã từng đi qua vùng đất này đều không thể bỏ lỡ những món ngon đặc sản nơi đây như nho, mực một nắng, tỏi, thịt cừu, thịt dông…'),
(44, 'Phú Thọ', 'Phú Thọ không chỉ là nơi đất tổ của các vị vua Hùng mà còn là mảnh đất của nhiều đặc sản độc đáo khó có thể tìm thấy được ở những nơi khác trên cả nước. Phú Thọ không chỉ có di tích lịch sử, những con sông, ngọn núi hùng vĩ. Đến với mảnh đất mưa nắng thuận hòa này, nhất định bạn phải nếm các món đặc sản nơi đây. Có lẽ, sự hấp dẫn của những món ăn dân dã chính là sợi dây níu chân bao du khách, đi rồi vẫn muốn trở lại đầy luyến tiếc. Giờ hãy cùng khám phá những món ngon đặc sản Phú Thọ mà du khách không thể bỏ qua khi đến đây nhé!'),
(45, 'Phú Yên', 'Nói đến Phú Yên ta không thể quên Gành Đá Đĩa, Đầm Ô Loan, Tháp Nhạn, Vũng Rô nơi huyền thoại về câu chuyện con tàu 0 số, và còn nhiều địa danh nổi tiếng khác. Lạc vào mảnh đất Phú Yên \"hoa vàng cỏ xanh\" đừng chỉ đắm chìm với cảnh đẹp mà bỏ quên rất nhiều món ăn ngon đặc sản nơi đây nhé. Món ăn ở Phú Yên có sự giao thoa giữa các vùng miền, tạo nên nhiều món ngon , lạ, và độc đáo.'),
(46, 'Quảng Bình', 'Quảng Bình là vùng đất trù phú với cảnh sắc thiên nhiên phong phú, nhiều danh lam thắng cảnh được tạo hóa ban tặng nên hàng năm lại đón hàng triệu lượt khách tới tham quan. Đặc sản Quảng Bình làm quà – nơi đây không chỉ được biết đến với những hang động hoang sơ, thác nước hùng vĩ mà còn khiến du khách “say lòng” bởi những món ăn đặc sản ngon miệng. Những món ăn đậm chất quê hương mộc mạc dưới đây sẽ mang đến cho bạn thêm nhiều trải nghiệm thú vị về đời sống văn hóa, tinh thần của người dân Quảng Bình. Vậy ăn ở đâu ngon khi đi du lịch Quảng Bình và địa chỉ các quán ăn đặc sản nổi tiếng ở Quảng Bình để bạn tới thưởng thức sau khi đi vui chơi là gì thì bạn hãy xem ngay danh sách dưới đây nhé!'),
(47, 'Quảng Nam', 'Tỉnh Quảng Nam nằm tại khúc ruột miền Trung, nơi đây không chỉ nổi tiếng với các địa danh Tam Kỳ hay Hội An mà còn thu hút bởi các đặc sản cực kỳ phong phú chỉ có tại Quảng Nam. Nổi tiếng với nhiều cảnh đẹp, Quảng Nam thu hút du khách trong nước và quốc tế với ẩm thực dân dã, đậm đà đầy ấn tượng.Từ lâu, xứ Quảng đã nổi tiếng gần xa với văn hóa ẩm thực đặc sắc. Và nhắc tới đặc sản Quảng Nam không thể không kể tới mì Quảng, cao lầu, bánh tổ…'),
(48, 'Quảng Ngãi', 'Ở đây, không chỉ chinh phục khách du lịch bằng cảnh sắc thiên nhiên đẹp đẽ mà còn đánh thức vị giác của chúng ta bằng những thức quà quê hương vô cùng giản đơn những không hề kém phần độc đáo và hấp dẫn. Những món ăn nơi đây làm cho ai đến đây cũng điều nhớ và hy vọng sẽ được quay lại một lần nữa.'),
(49, 'Quảng Ninh', 'Văn hoá ẩm thực ở vùng biển Quảng Ninh có đặc điểm là cùng một món ăn nhưng lại có nét riêng biệt, mang đặc trưng của vùng mình. Ví dụ như vùng huyện đảo Vân Đồn, Cô Tô, Quảng Yên đều có mực ống, hà, tù hài... Khi đến với Quảng Ninh, một vùng đất giàu tính nhân văn mang đậm nét văn hoá Việt, bạn bè gần xa lại có dịp thưởng thức những món ăn dân gian khó quên, những món ăn ở Quảng Ninh, một phần nét ẩm thực của vùng biển Đông Bắc Việt Nam.'),
(50, 'Quảng Trị', 'Quảng Trị, mảnh đất nơi dải đất miền Trung, thời tiết khắc nghiệt nắng lắm, mưa nhiều, nhưng lại có một nền văn hóa ẩm thực phong phú, đa dạng và vô cùng tinh tế. Để hiểu thêm về con người, văn hóa sống của vùng đất này, bạn đừng quên khám phá ẩm thực Quảng Trị qua các món ăn chứa đựng cả nhân sinh quan của con người nơi đâyQuảng Trị tuy không được mệnh danh là địa phương của tinh hoa ẩm thực, thế nhưng nơi đây cũng có nhiều món ăn trứ danh, đặc sắc, làm “mát lòng” du khách mỗi khi có dịp dừng chân. Những món ăn này được chắt chiu từ thời tiết khắc nghiệt nên mang hương vị riêng biệt, khó hòa lẫn với các vùng đất khác trên Tổ quốc.'),
(51, 'Sài Gòn', '<p>TỔNG HỢP ĐẶC SẢN SÀI GÒN: TẤT CẢ 12 ĐẶC SẢN NỔI TIẾNG TP. HCM</p>\r\n<p>Tất cả 13 đặc sản Sài Gòn được VietFlavour tổng hợp, trong đó không ít món thân quen và gây thương nhớ không chỉ đối với người Sài Gòn xa quê mà còn đối với mọi người xem Sài Gòn là quê hương thứ hai…</p>'),
(52, 'Sóc Trăng', 'Từ xưa tới nay, đặc sản Sóc Trăng luôn là nét hấp dẫn riêng biệt hấp dẫn khách thập phương đến với mảnh đất miền Nam này. Ai đã một lần đặt chân tới đây, chắc chắn sẽ không quên được dư vị của những thức quà giản dị nhưng khó tìm này. Đến với vùng đất Sóc Trăng , bạn sẽ được thưởng thức những đặc sản món ngon nổi tiếng cả nước như vú sữa tím, bánh pía, khô trâu, mè láo, hủ tíu cà ri…'),
(53, 'Sơn La', 'Sơn La là vùng đất sinh sống lâu đời của 12 dân tộc anh em, mỗi dân tộc đều có những bản sắc văn hóa riêng, độc đáo và tương đồng có lẽ vậy mà các món ăn ngon tại Sơn La mang những hương vị không trộn lẫn, có từ ngàn đời nay. Do có cộng đồng người Thái sinh sống đông nhất cả nước, ẩm thực Sơn La mang nhiều nét đặc trưng của dân tộc Thái. Hiện nay, một số món ăn của người Thái đã trở thành các sản phẩm để giới thiệu tại các nhà hàng, khách sạn, phục vụ khách du lịch đến Sơn La và được quảng bá ở một số tỉnh, thành phố trong cả nước.'),
(54, 'Tây Ninh', 'Tây Ninh cách Sài Gòn khoảng 100 km về phía tây bắc, là điểm đến thú vị cho những chuyến du lịch ngắn ngày hay dã ngoại cùng bạn bè. Ngoài những địa điểm du lịch hấp dẫn, bạn đừng quên thưởng thức 10 món ăn dân dã nhưng vô cùng hấp dẫn dưới đây: Bánh canh Trảng Bàng, Bánh tráng phơi sương cuốn thịt, Món ăn chay Tây Ninh, Bò tơ Củ Chi, Mắm chua Tây Ninh, Ốc xu núi Bà, Thằn lằn núi Bà Đen, Muối tôm, Nem bưởi, Bánh tráng me.'),
(55, 'Thái Bình', 'Những món đặc sản Thái Bình không chỉ gây thương nhớ với những người con xa quê, mà ngay cả với những du khách dù chỉ được ăn một lần cũng sẽ nhớ mãi những hương vị thơm ngon ấy. Thái Bình nổi tiếng với bánh cáy, với canh cá Quỳnh Côi… Ngoài ra, nơi đây còn có nhiều món đặc sản thơm ngon, mang đậm đà hương vị mộc mạc mà tinh tế của vùng đất dân dã ấy.'),
(56, 'Thái Nguyên', 'Thái Nguyên nằm ở phía Đông Bắc của Tổ quốc, là một trong những trung tâm văn hóa - kinh tế - chính trị trọng yếu của khu vực Đông Bắc cũng như vùng trung du miền núi phía Bắc. Thái Nguyên không chỉ là vùng đất được du khách biết đến với nhiều danh làm thắng cảnh và di tích lịch sử, mà còn là nơi có văn hóa ẩm thực vô cùng độc đáo với nhiều đặc sản mang đậm tinh hoa núi rừng.'),
(57, 'Thanh Hóa', '<p>TỔNG HỢP ĐẶC SẢN THANH HÓA: TẤT CẢ 21 ĐẶC SẢN NỔI TIẾNG NHẤT XỨ THANH</p>\r\n<p>Mảnh đất Thanh Hóa xưa giờ vốn nổi tiếng là vùng đất “địa linh nhân kiệt”. Thế nên du lịch Thanh Hóa không chỉ là thưởng ngoạn cảnh đẹp nên thơ, những con người hiếu khách. Mà còn là dịp con người ta trải lòng với những di tích lịch sử. Và nếu đã từng ghé thăm chắc rằng bạn cũng khó có thể bỏ qua nét độc đáo trong ẩm thực xứ Thanh.</p>'),
(58, 'Huế', 'Huế nổi tiếng với ẩm thực cung đình cao sang và mĩ vị, tuy nhiên các gánh hàng rong, các món ăn bình dân cũng có sức hấp dẫn khó cưỡng. Bánh canh, bánh nậm, bánh bèo hay bánh ướt... là những món ăn vặt hấp dẫn mà du khách mỗi khi đến Huế thường muốn thưởng thức ít nhất một lần để hiểu hơn về ẩm thực miền cố đô.'),
(59, 'Tiền Giang', '<p>TỔNG HỢP ĐẶC SẢN TIỀN GIANG: TẤT CẢ 16 ĐẶC SẢN NỔI TIẾNG CỦA TIỀN GIANG</p>\r\n<p>Tất cả 16 đặc sản Tiền Giang được tổng hợp hết trong bài viết sẽ giúp bạn cảm nhận được hết nét đẹp trong văn hóa ẩm thực và con người Tiền Giang.</p>\r\n<p>Xưa nay nhắc đến Tiền Giang người liền nghĩ ngay đến vùng đất có nhiều Hoàng Hậu nhất xứ Nam Kỳ. Nơi đây thu hút du khách với các di tích lịch sử cùng những loại hình du lịch đậm chất miệt vườn. Tiền Giang còn mang nhiều thú vị khi dành tặng du khách nhiều món ăn hấp dẫn, đặc trưng của riêng vùng đất này.</p>'),
(60, 'Trà Vinh', '<p>TỔNG HỢP ĐẶC SẢN TRÀ VINH: TẤT CẢ 17 ĐẶC SẢN NỔI TIẾNG CỦA TRÀ VINH</p>\r\n<p>Tất cả 17 đặc sản của Trà Vinh được VietFlavour tổng hợp sẽ giúp bạn được trải nghiệm hết hương vị truyền thống, các đặc sản Trà Vinh nổi bật nhất.</p>'),
(61, 'Tuyên Quang', '<p>TỔNG HỢP ĐẶC SẢN TUYÊN QUANG: TẤT CẢ 18 ĐẶC SẢN CỦA XỨ TUYÊN”</p>\r\n<p>Tổng hợp tất cả 18 đặc sản Tuyên Quang được VietFlavour tổng hợp sẽ mang tới cho bạn đọc một cái nhìn chi tiết về các đặc sản của Tuyên Quang.</p>\r\n<p>Tuyên Quang nằm ở khu vực trung tâm của Tây Bắc. Nơi đây không chỉ được thiên nhiên ưu đãi cho bao phong cảnh hữu tình mà còn là cái nôi cách mạng của dân tộc. Bên cạnh đó sự đa dạng về dân tộc đa giúp Tuyên Quang có thêm nhiều sắc màu văn hóa.</p>'),
(62, 'Vĩnh Long', 'Vĩnh Long không chỉ có những điểm tham quan đẹp và nổi tiếng cùng với đó là vô số các món ăn ngon. Đi du lịch ngoài mục đích được nghỉ ngơi, thư giãn, ngắm nhìn các cảnh đẹp…thì các du khách còn muốn thưởng thức các món ăn đặc sản tại nơi những nơi mình đặt chân đến. Nếu các bạn đang có dự định du lịch tại Vĩnh Long mà chưa biết thưởng thức các món đặc sản của vùng đất này ở đâu thì hãy bỏ túi ngay top 10 món ăn sau: Cá tai tượng chiên xù, Khoai lang mắm sống cuốn lá cách, Cá lóc nướng trui, Bánh tráng nem cù lao, Cá út nấu canh chua, Các món ngon lạ từ nấm mối, Cá cóc kho nước dừa, cá cóc nấu canh chua.'),
(63, 'Vĩnh Phúc', '<p>TỔNG HỢP ĐẶC SẢN VĨNH PHÚC: TẤT CẢ 16 ĐẶC SẢN NỔI TIẾNG NHẤT VĨNH PHÚC</p>\r\n<p>Tất cả 16 đặc sản Vĩnh Phúc được VietFlavour dày công tổng hợp sẽ ít nhiều giúp bạn cảm nhận hết cái hồn trong từng đặc sản của Vĩnh Phúc.</p>'),
(64, 'Yên Bái', '<p>TỔNG HỢP ĐẶC SẢN YÊN BÁI: TẤT CẢ 29 MÓN ĐẶC SẢN NỔI TIẾNG CỦA YÊN BÁI</p>\r\n<p>Tổng hơp tất cả các đặc sản Yên Bái gồm 29 món đặc sản không chỉ nổi tiếng mà còn gây thương nhớ cho mỗi người con Yên Bái lẫn du khách thập phương.</p>\r\n<p>Yên Bái là một tỉnh vùng núi tiếp giáp Tây Bắc và Đông Bắc nước ta. Nhắc tới Yên Bái người ta lại không khỏi xao xuyến khi nhớ về những thửa ruộng bậc thang đẹp như tranh; những biển mây trắng ôm xòa lòng người hay chỉ đơn giản là hương vị núi rừng mộc mạc.\r\n\r\n</p>\r\n<p>Ấy vậy mà nét quyến luyến của Yên Bái không chỉ dừng lại ở cái đẹp mãn nhãn. Yên Bái còn níu chân du khách với biết bao món đặc sản ngon khó cưỡng. Hãy cùng VietFlavour khám phá xem đó là những món đặc sản nào nhé!</p>'),
(65, 'Bắc Kạn', 'Ngoài cảnh đẹp non nước hữu tình của hồ Ba Bể, Bắc Kạn còn có những đặc sản dân tộc đặc trưng khiến bước chân lữ khách du lịch nơi đây phải lưu luyến. Đến với Bắc Kạn, các bạn không những có cơ hội tham quan khung cảnh thiên nhiên tuyệt đẹp mà còn được thưởng thức các loại đặc sản mang đậm bản sắc của đồng bào dân tộc nơi đây.');

